import React, { useState } from 'react';
import {View, Text, StyleSheet, Touchable, TouchableOpacity, Dimensions} from 'react-native';
import Ionicons from 'react-native-vector-icons/Ionicons';

interface Props {
    name: String,
    text: String,
    likedBy: Array<String>,
}

const windowWidth = Dimensions.get('window').width;

export default function FeedsListItem(data: Props) {
    const [iconState, setIcon] = useState('heart-outline');
    const handlePressName = () => {
        alert(data.name)
    };
    const handlePressContent = () => {
        alert(data.text)
    }
    const handlePressLike = () => {
        // Toggle between like and unlike
        if(iconState === 'heart') {
            setIcon('heart-outline')
        } else {
            setIcon('heart')
        }
    }

    const likedByToNumberOfLikes = (likedBy: Array<String>) => {
        console.log(likedBy.length)
        return likedBy.length
    }

    return (
        
        <View style={[styles.container, styles.elevation]}>
            <TouchableOpacity onPress={handlePressName} style={styles.top}>
                <Text style={styles.header}>
                    {data.name}
                </Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.content} onPress={handlePressContent}>
                <Text>
                    {data.text}
                </Text>
            </TouchableOpacity>
            <Ionicons name={iconState} style={styles.likesIcon} size={20} onPress={handlePressLike}/>
            <Text style={styles.likesText}>
                {likedByToNumberOfLikes(data.likedBy)}
            </Text>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        backgroundColor: '#404040',
        margin: 12,
        width: (windowWidth / 2) - 40,
        flex: 1,
        borderRadius: 15,
    },
    header: {
        color: '#cccccc',
        fontSize: 20,
    },
    top: {
        backgroundColor: 'rgba(40, 40, 40, 0.3)',
        padding: 5,
        paddingLeft: 8,
        borderTopLeftRadius: 15,
        borderTopRightRadius: 15,
    },
    content: {
        margin: 20,
    },
    likesIcon: {
        color: '#cccccc',
        position: 'absolute',
        right: 30,
        bottom: 8,
        zIndex: 3,
        elevation: 3,
    },
    likesText: {
        color: '#909090',
        fontSize: 13,
        position: 'absolute',
        right: 10,
        bottom: 10,
        zIndex: 3,
        elevation: 3,
    },
    shadowProp: {
        shadowColor: '#171717',
        shadowOffset: {width: -2, height: 4},
        shadowOpacity: 0.2,
        shadowRadius: 3,
    },
    elevation: {
        elevation: 20,
        shadowColor: '#000000',
    },
});
